const WEI_DECIMAL_PLACES = 18;

// TODO: Super insecure!
const DEFAULT_PASSPHRASE = "passphrase";

// TODO: temporary hack, because of RSK whitelisitng
const BANK_ADDRESS = "0x0e082742330d4a06ef127ca89f78f7283141c572";

const toWei = etherValue => new BigNumber(etherValue).shift(WEI_DECIMAL_PLACES);

const toEther = weiValue => new BigNumber(weiValue).shift(-WEI_DECIMAL_PLACES);

export default class RSKService {
  constructor(ownerAddress, ownerPrivateKey) {    
    this.provider = new Web3.providers.HttpProvider(config.rsk.url);
    this.web3 = new Web3(this.provider);

    // TODO: this is hack to avoid signing transactions - NOT SECURE!    
    this.login(ownerAddress, ownerPrivateKey);  
  }

  login = (ownerAddress, ownerPrivateKey) => {
    if (ownerPrivateKey) {
      this.web3.personal.importRawKey(ownerPrivateKey, DEFAULT_PASSPHRASE);    
    }
    if (ownerAddress) {
      this.web3.personal.unlockAccount(ownerAddress, DEFAULT_PASSPHRASE);
    }    
    this.ownerAddress = ownerAddress;
    this.ownerPrivateKey = ownerPrivateKey;
  }

  deployCrowdsale = (config) => {
    const {
      tokenName,
      tokenSymbol,
      startTime,
      endTime,
      rate,
      goal,
      cap,
      wallet,
      onSent,
    } = config;
    console.log({
      tokenName,
      tokenSymbol,
      startTime: Math.round(startTime.getTime() / 1000),
      endTime: Math.round(endTime.getTime() / 1000),
      rate: toWei(rate),
      goal: toWei(goal),
      cap: toWei(cap),
      wallet,
      txConfig: {
        from: this.ownerAddress,        
        gas: 6000000,
        gasPrice: 1,
      }
    });
    return new Promise((resolve, reject) => {
      let Crowdsale = this.web3.eth.contract(CROWDSALE_CONFIG.abi);

      let crowdsaleInstance = Crowdsale.new(
        tokenName,
        tokenSymbol,
        Math.round(startTime.getTime() / 1000),
        Math.round(endTime.getTime() / 1000),
        toWei(rate),
        toWei(goal),
        toWei(cap),
        wallet,
        {
          from: this.ownerAddress,
          data: CROWDSALE_CONFIG.bytecode,
          gas: 6000000,
          gasPrice: 1,
        },
        (e, contract) => {
          if (!e) {
            if (!contract.address) {
              if (onSent) {
                onSent(contract);
              }
              console.log(`Contract transaction send: TransactionHash: ${  contract.transactionHash  } waiting to be mined...`);
                    
            } else {
              this.loadCrowdsaleAt(contract.address);
              resolve(contract);
              console.log(`Contract mined! Address: ${  contract.address}`);            
            }
          }
        },
      );
      this.crowdsale = crowdsaleInstance;
    });
  }

  acceptBTCTransfer = ( to, etherAmount ) => {
    // TODO: temporary hack, because of RSK whitelisitng
    return this.transferEther(BANK_ADDRESS, to, etherAmount);
  };

  transferEther = (from, to, etherAmount) =>
    this.web3.eth.sendTransaction({
      from: from,
      to: to,
      value: toWei(etherAmount),
      gas: 6000000,
      gasPrice: 1,
    });

  loadCrowdsaleAt = contractAddress => {
    const Crowdsale = this.web3.eth.contract(CROWDSALE_CONFIG.abi);
    this.crowdsale = Crowdsale.at(contractAddress);
    this.loadTokenAt(this.crowdsale.token());
  };

  createAccount = async () => {    
    const account = await this.web3.personal.newAccount(DEFAULT_PASSPHRASE);
    this.transferEther(BANK_ADDRESS, account, 1e-4);    
    return account;
  }

  loadTokenAt = contractAddress => {
    const Token = this.web3.eth.contract(TOKEN_CONFIG.abi);
    this.token = Token.at(contractAddress);
  };

  buyTokens = (beneficiary, value) => {    
    return this.crowdsale.buyTokens(beneficiary, {
      value: toWei(value),
      from: this.ownerAddress,
      gas: 6000000,
      gasPrice: 1,
    });
  };

  finalizeCrowdsale = () =>
    this.crowdsale.finalize({
      from: this.ownerAddress,
      gas: 6000000,
      gasPrice: 1,
    });

  tokenBalance = address => toEther(this.token.balanceOf(address));
}
